        };
    }
    ngOnInit() {
    }
    async connectAccount() {
        let user = await this.contractService.connectAccount();
        console.log(user.status);
        if (user.status === 'exists') {
            console.log('user already exists');
            this.router.navigate(['/']);
        }
        else
